.progress {
  margin-bottom: rhythm(1);
  position: relative;
}

.progress__track {
  @include background-image(
    linear-gradient(
      top,
      $gray90 0%,
      $gray90 100%
    )
  );
  border-radius: rem(16px);
  background-color: #fff;
  box-shadow: inset 0 1px 3px rgba($gray25, .2), inset 0 -1px 0 rgba($gray25, .05);
  height: rem(16px);
  margin-bottom: rhythm(1/4);
  max-width: 100%;
  min-width: rem(96px);
  overflow: hidden;
}

.progress__bar {
  @include background-image(
    linear-gradient(
      left,
      lighten($link-color, 10%) 0%,
      darken($link-color, 10%) 100%
    )
  );
  border-radius: rem(16px);
  @include transition(width .5s ease-out);
  background-color: lighten($link-color, 15%);
  border: 1px solid darken($link-color, 5%);
  height: rem(16px);
  width: 3%;
}

