  @include clearfix;
  margin-left: - $gutter/2;
  margin-right: - $gutter/2;
}

.l-grid__column {
  margin-bottom: rhythm(1);
  float: left;
  width: 100%;
  box-sizing: border-box;

  @include media($b-medium, $no-mq) {
    @include pad($gutter/2);
  }

  .l-grid--cols-2 & {
    @include media($b-medium, $no-mq) {
      width: (1/2) * 100%;
    }
  }
  .l-grid--cols-3 & {
    @include media($b-medium, $no-mq) {
      width: (1/3) * 100%;
    }
  }
  .l-grid--cols-4 & {
    @include media($b-medium, $no-mq) {
      width: (1/4) * 100%;
    }
  }
  .l-grid--cols-5 & {
    @include media($b-medium, $no-mq) {
      width: (1/5) * 100%;
    }
